export default ({
    center,
    vCenter,
    dark,
    gray,
    wide,
    black,
    small,
    padding,
    overflow,
    minHeight,
    dotBackground,
    children,
    mobileStyle,
    ...props
  }) => (
    <div {...props}>
      <style jsx>
        {`
        {
          width: 100%;
          margin: 0 auto;
          padding: ${padding ? '4rem' : '0'} ${wide ? '0' : '1rem'};
          ${wide && !small ? '' : 'max-width: 1024px;'}
          ${small ? 'max-width: 682px;' : ''}
          ${center ? 'text-align: center;' : ''}
          ${
            dark
              ? 'background-image: linear-gradient(to bottom, #6200EE  0%, #323232 100%);'
              : ''
          }
          ${dark ? 'color: #f1f1f1;' : ''}
          ${black ? 'background-color: #6200EE;' : ''}
          ${gray ? 'background-color: #f6f6f6;' : ''}
          ${wide && !overflow ? 'overflow: hidden;' : ''}
          ${minHeight ? `min-height: ${minHeight}px;` : ''}
          ${vCenter ? 'display: flex; align-items: center;' : ''}
          ${
            dotBackground
              ? `
            background-image: radial-gradient(circle, #D7D7D7, #D7D7D7 .8px, #FFF .8px, #FFF);
            background-size: 28px 28px;
          `
              : ''
          }
        }
        :after {
          // BFC
          content: '';
          display: table;
          clear: both;
        }
        
        // CSS only media query for tablet
        @media screen and (max-width: 960px) {
          div {
            padding: ${padding ? '4rem' : '0'} ${wide ? '0' : '2rem'};
          }
        }
        // CSS only media query for mobile
        @media screen and (max-width: 640px) {
          div {
            padding: ${padding ? '4rem' : '0'} ${wide ? '0' : '1rem'};
            ${mobileStyle || ''}
          }
        }
      `}
      </style>
      {children}
    </div>
  );
