import React, { useState, useEffect, useRef } from "react";
import axios from 'axios';
import { deflateSync } from 'zlib';
import Web3 from 'web3';
import Fortmatic from 'fortmatic';

var web3;
var state;
var captureFile;
var onSubmit;

var address;
let fm = new Fortmatic('pk_test_F4970AF6BBC7F0C1');

if (process.browser) {
  web3 = new Web3(fm.getProvider());
}


function useAsyncEndpoint(fn) {
  const [res, setRes] = useState({
    data: null,
    pending: deflateSync,
    completed: false,
    error: false,
  });
  const [req, setReq] = useState();

  state = {
    ipfsHash:null,
    buffer:'',
    ethAddress:'',
    transactionHash:'',
    txReceipt: ''
  };

//Take file input from user
 captureFile =(event) => {
      event.stopPropagation()
      event.preventDefault()
      const file = event.target.files[0]
      let reader = new window.FileReader()
      reader.readAsArrayBuffer(file)
      reader.onloadend = () => this.convertToBuffer(reader)
    };


onSubmit = async (event) => {
      event.preventDefault();
      const accounts = await web3.eth.getAccounts();
    //save document to IPFS,return its hash#, and set hash# to state
      await ipfs.add(this.state.buffer, (err, ipfsHash) => {
        console.log(err,ipfsHash);
        //setState by setting ipfsHash to ipfsHash[0].hash
        this.setState({ ipfsHash:ipfsHash[0].hash });
        storehash.methods.sendHash(this.state.ipfsHash).send({
          from: accounts[0]
        }, (error, transactionHash) => {
          console.log(transactionHash);
          this.setState({transactionHash});
        });
      })
    };
  
  useEffect(() => {
    if (!req) return;
    setRes({
      data: null,
      pending: true,
      completed: false,
      error: false,
    });
  axios(req)
  .then(res =>
    setRes({
      data: res.data,
      pending: false,
      error: false,
      complete: true
    }),
  )
  .catch(() =>
    setRes({
      data: null,
      pending: false,
      error: true,
      complete: true
    }),
  );
}, [req]
);
return [res, (...args) => setReq(fn(...args))];

}

let verifyData;

let handlePersonalSign = (e) => {
  let message = message;
  web3.eth.getAccounts((err, accounts) => {
    if (err) return console.error(err);
    var from = accounts[0];
    var params = [message, from];
    var method = 'personal_sign';
    web3.currentProvider.sendAsync({
      id: 1,
      method,
      params,
      from,
    }, (err, result) => {
      if (err) return console.error(err);
      if (result.error) return console.error(result.error);
      console.log(result);
    })
  });
};



const publisherAPI = "http://ec2-18-204-34-34.compute-1.amazonaws.com:5000/upload";

function postPublisherEndpoint() {
  
  return useAsyncEndpoint(data => ({
  url: publisherAPI,
  method: "POST",
  data
}));
}

export default function MessageBar(props) {
  const [message, setMessage] = useState("");
  const [to, setTo] = useState("");
  const [who, setWho] = useState("");
  const [newMessage, postNewMessage] = postPublisherEndpoint();

  const handleKeyPress = (event) => {
  if(event.key == 'Enter'|| event.keyCode == 13) {
    handlePersonalSign();
  }
}

return (
    <section>
    <div className="row">
        <div className="column">
        <form id="ipfs-hash-form">
        <input className="ipfsInput"
        type = "file"
      
        />
        <button className="policyButton" onClick="onSubmit"> 
        Upload
        </button><img className="center" src="../static/verification.png" height="28" width="28" align="middle" alt=""/>
        </form>
        
        <br/>
        <input className="message-box" value={message} onChange={e => setMessage(e.target.value)} onKeyPress={handleKeyPress}  /> 
        <a><button onClick={verifyData} className="policyButton" invert >Verify</button><img className="center" src="../static/verification.png" height="28" width="28" align="middle" alt=""/></a>
        </div>
        
        </div>
        <style jsx>{`

      .column {
        text-align: center;
        border-width: 1px;
        list-style: none;
        padding-bottom: 8px;
        padding-top: 8px;
        margin-right: 28px;
      }

      .center {
        margin-left: 3px;
        margin-bottom: 10px;
      }

      .to {
        width: 260px;
        padding-bottom: 8px;
        padding-top: 8px;
        border: 1px solid #d0d4d9; 
        border-radius: .375rem;
        -webkit-box-shadow: inset 0 -1px 0 0 rgba(100,121,143,0.122);
        box-shadow: inset 0 -1px 0 0 rgba(100,121,143,0.122);
        line-height: 20px;
        min-height: auto; 
        padding-left: 0; 
        padding-right: 0;
      }

      .policyButton {
        -webkit-appearance: none;
        position: relative;
        display: inline-block;
        vertical-align: middle;
        text-transform: uppercase;
        text-align: center;
        line-height: 0;
        white-space: nowrap;
        width: 120px;
        height: 30px;
        margin-left: 8px;
        font-weight: 500;
        font-size: 12px;
        color: rgb(102, 102, 102);
        background-color: rgb(255, 255, 255);
        user-select: none;
        cursor: pointer;
        text-decoration: none;
        padding: 0px 10px;
        margin-bottom: 2px;
        border-radius: 5px;
        border-width: 1px;
        border-style: solid;
        border-color: rgb(234, 234, 234);
        border-image: initial;
        transition: all 0.2s ease 0s;
        overflow: hidden;
        outline: none;
      }

      
      .ipfsInput {
        -webkit-appearance: none;
        position: relative;
        display: inline-block;
        vertical-align: middle;
        text-transform: uppercase;
        text-align: center;
        line-height: 0;
        white-space: nowrap;
        width: 300px;
        height: 30px;
        margin-left: 8px;
        font-weight: 500;
        font-size: 12px;
        color: #000;
        user-select: none;
        cursor: pointer;
        text-decoration: none;
        padding: 0px 10px;
        margin-bottom: 2px;
        border-radius: 5px;
        border-width: 1px;
        border-style: solid;
        border-color: rgb(234, 234, 234);
        border-image: initial;
        transition: all 0.2s ease 0s;
        overflow: hidden;
        outline: none;
        
      }

      .message-box {
        padding-bottom: 28px;
        width: 800px ;
        overflow: auto;
        margin: 0; 
        min-height: 42px; 
        height: 38px; 
        max-height: 180px; 
        border: 2px solid rgba(134,134,134,.7);
        border-radius: .375rem;
        outline: 0;
        background: #fff;
        resize: none;
        box-shadow: none;
        color: #1d1c1d;
        -webkit-user-select: auto;
        -moz-user-select: auto;
        -ms-user-select: auto;
        user-select: auto;
        padding: 9px 30px 10px 50px;"
      }

      .login {
        color: #FFF;
      }

      .account {
        font-size: 13px;
        line-height: 1.91667;
        font-weight: 400;
        color: #3d464d;
        border: 1px solid #bdc4c9;
        padding: 3.5px 7px;
        border-radius: 4px;
        margin-top: 28px;
        margin-left: 8px;
      }

      .row {
        flex-direction: column;
        margin: -1.5rem 0;
        text-align: center;
      }

      // CSS only media query for tablet
      @media screen and (max-width: 960px) {
        .row {
          flex-direction: column;
          margin: -1.5rem 0;
        }
        .column {
          width: 10%;
          padding: 1.5rem 0;
          text-align: center;
          max-width: 100px;
        }

        .message-box {
          padding-bottom: 28px;
          width: 100px;
          overflow: auto;
          margin: 0; 
          min-height: 42px; 
          height: 38px; 
          max-height: 180px; 
          border: 2px solid rgba(134,134,134,.7);
          border-radius: .375rem;
          outline: 0;
          background: #FFFFFF;
          resize: none;
          box-shadow: none;
          color: #1d1c1d;
          -webkit-user-select: auto;
          -moz-user-select: auto;
          -ms-user-select: auto;
          user-select: auto;
          padding: 9px 30px 10px 50px;
          display: none;
        }

        
      }
    `}</style>
        </section>
)

    }
