import React from 'react'
import axios, { post } from 'axios';

class SimpleReactFileUpload extends React.Component {

  constructor(props) {
    super(props);
    this.state ={
      file:null,
      policy:''
    }
    this.onFormSubmit = this.onFormSubmit.bind(this)
    this.onChange = this.onChange.bind(this)
    this.fileUpload = this.fileUpload.bind(this)
  }
  onFormSubmit(e){
    e.preventDefault() // Stop form submit
    this.fileUpload(this.state.file).then((response)=>{
      console.log(response.data);
    })
  }
  onChange(e) {
    this.setState({file:e.target.files[0]})
  }
  fileUpload(file){
    const url = "https://app.triplecheck.network/upload";
    const formData = new FormData();
    formData.append('policy', policy)
    formData.append('file',file)
    const config = {
        headers: {
            'content-type': 'multipart/form-data'
        }
    }
    return  post(url, formData,config)
  }

  render() {
    return (
      <form onSubmit={this.onFormSubmit}>
        <input type="file" onChange={this.onChange} />
        <select id="policy" className="policyDropdown" value={this.state.policy} onChange={(e) => this.setState({policy: e.target.value, validationError: e.target.value === "" ? "You must select a policy" : ""})} >
          <option value="policy1">Policy 1</option>
          <option value="policy2">Policy 2</option>
        </select>
        <button type="submit">Upload</button>
      </form>
   )
  }
}



export default SimpleReactFileUpload
