import React, { useState, useContext, useEffect } from 'react';
import usePolicyForm from '../usePolicyForm';

export default function PolicyForm(props) {

    const {inputs, handleInputChange, handleSubmit} = usePolicyForm();

    const usePolicyForm = (callback) => {
        const [inputs, setInputs] = useState({});
        const handleSubmit = (event) => {
          if (event) {
            event.preventDefault();
          }
        }
        const handleInputChange = (event) => {
          event.persist();
          setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
        }
        return {
          handleSubmit,
          handleInputChange,
          inputs
        };
      }


    const policyName = useFormInput('')
    const policyExpirationDate = useFormInput('')
    const policyPassword = useFormInput('');

return (
    <section>
    <div className="row">
    <h1>New Policy</h1>
    <div className="column">
        <label >Policy Name:</label>
        <input className="policy" {...policyName} />
        <br/>
        <label >Policy Expiration Date: </label>
        <input className="policy" {...policyExpirationDate} />
        <br/>
        <label> Policy Password: </label>
        <input className="policy" type="password" {...policyPassword} />
        <br/>
        <br/>
        </div>
        <button className="policyButton" invert>Submit</button>
    </div>
        <style jsx>{`
      .column {
        text-align: left;
        border-width: 1px;
        list-style: none;
        padding-bottom: 8px;
        padding-top: 8px;
        margin-right: 28px;
        color: #FFF;
      }

      .policyButton {
        -webkit-appearance: none;
        position: relative;
        display: inline-block;
        vertical-align: middle;
        text-transform: uppercase;
        text-align: center;
        line-height: 0;
        white-space: nowrap;
        width: 120px;
        height: 30px;
        margin-left: 8px;
        font-weight: 500;
        font-size: 12px;
        color: rgb(102, 102, 102);
        background-color: rgb(255, 255, 255);
        user-select: none;
        cursor: pointer;
        text-decoration: none;
        padding: 0px 10px;
        margin-bottom: 2px;
        border-radius: 5px;
        border-width: 1px;
        border-style: solid;
        border-color: rgb(234, 234, 234);
        border-image: initial;
        transition: all 0.2s ease 0s;
        overflow: hidden;
        outline: none;
      }

      h1 {
        font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;
        margin: 10px 0;
        font-size: 1.7em;
        font-weight: 200;
        margin-right: 30px;
        padding-left: 8px;
        color: #FFF;
        text-align: left;
      }

      .login {
        color: #FFF;
      }

      h2 {
        font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;
        margin: 10px 0;
        font-size: 24px;
        font-weight: normal;
        margin-right: 30px;
        padding-left: 5px;
        color: #FFF;
        text-align: center;
      }

      h3 {
        font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;
        margin: 10px 0;
        font-size: 17px;
        font-weight: normal;
        margin-right: 30px;
        padding-left: 5px;
        color: #FFF;
        text-align: center;
      }


      .policy {
        font-size: 13px;
        line-height: 1.91667;
        font-weight: 400;
        color: #3d464d;
        border: 1px solid #bdc4c9;
        padding: 3.5px 7px;
        border-radius: 4px;
        margin-top: 28px;
        margin-left: 8px;
      }

      .row {
        flex-direction: column;
        margin: -1.5rem 0;
        text-align: center;
      }

      // CSS only media query for tablet
      @media screen and (max-width: 960px) {
        .row {
          flex-direction: column;
          margin: -1.5rem 0;
        }
        .column {
          width: 100%;
          padding: 1.5rem 0;
          text-align: center;
          max-width: 350px;
        }

        h2 {
            font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;
            margin: 10px 0;
            font-size: 24px;
            font-weight: normal;
            margin-right: 30px;
            padding-left: 5px;
            color: #FFF;
            text-align: center;
          }

        
      }
    `}</style>
    </section>
  );
}


function useFormInput(initialValue) {
    const [value, setValue] = useState(initialValue);

    function handleChange(e) {
        setValue(e.target.value)
    }

    return {
        value,
        onChange: handleChange
    };
}

function useDocumentTitle(title) {
    useEffect(() => {
        document.title = title;
    });
}

function useFetch(url) {
  const [data, setData] = useState([]);
  
  async function getData() {
    const response = await fetch(url);
    const data = await response.json();
    setData(data);
  }

  useEffect(() => {
    getData();
  }, [])
}
